import React, { useState } from 'react';

const GymApp = () => {
  const [user, setUser] = useState(null);
  const [exercises, setExercises] = useState([
    { id: 1, name: 'Push-ups', description: 'Bodyweight exercise for the chest and arms' },
    { id: 2, name: 'Squats', description: 'Lower body exercise targeting the quads and glutes' },
    { id: 3, name: 'Planks', description: 'Core exercise for building abdominal strength' },
  ]);

  const handleLogin = (username) => {
    setUser({ username });
  };

  const handleLogout = () => {
    setUser(null);
  };

  return (
    <div>
      <h1>Gym App</h1>
      {user ? (
        <div>
          <p>Welcome, {user.username}!</p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <div>
          <p>Please log in</p>
          <button onClick={() => handleLogin('JohnDoe')}>Log in as JohnDoe</button>
        </div>
      )}

      <h2>Exercise List</h2>
      <ul>
        {exercises.map(exercise => (
          <li key={exercise.id}>
            <strong>{exercise.name}</strong>: {exercise.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default GymApp;
