import { useEffect, useState } from "react";
import { Link } from "react-router-dom";





function Addcart() {
    const [data, setData] = useState([])
    const [total,setTotal]=useState()
    useEffect(() => {
        fetch("http://localhost:3006/addcart")
            .then((go) => {
                return go.json()
            })
            .then((resp) => {
                setData(resp)

                const total=resp.reduce((og,item)=>og+parseFloat(item.price),0)
                setTotal(total)
            })
    }, [])
    const Delete=(id)=>{
   fetch("http://localhost:3006/addcart/"+id,{
    method:"DELETE"
   })
   .then(()=>{
    // alert("Removed successfully")
   })
   window.location.reload()
   
    }




    return (
        <div>
            <Link to="/app">
            <h1 style={{textAlign:"center "}}>GO BACK TO HOME PAGE</h1>
            </Link>
            {data.map((item) => (
                <div className="card" style={{ width: "18rem", boxShadow: "gray 5px 5px 5px", display:"grid", gridTemplateColumns:"auto auto auto" }}>

                    <img src={item.img} class="card-img-top" alt="..." style={{ height: "200px", }} />

                    <div className="card-body">

                        <h5 className="card-title">{item.brand}</h5>
                        <p className="card-text">{item.size}</p>
                        <p>{item.description}</p>
                        <h3 >Rs: {item.price}</h3>
                        <a href="#" className="btn btn-primary"  onClick={()=>{Delete(item.id)}}>Remove(-)</a>
                    </div>

                </div>

            ))}
            <br/>

            <h2 style={{marginLeft:"1100px"}}>Total:{total}</h2>
        </div>
    )
}
export default Addcart;
